import React, { Component } from 'react';
import './App.css';
// import TracksContainer from './components/TracksContainer'



class App extends Component {

    constructor(props) {
      super(props);
      this.state = { apiResponse: "",
      tracks: [],
      search: '',
      artist: null,
      searchType: 'album',};
    }

    callAPI() {
      fetch("http://localhost:9000/testAPI")
          .then(res => res.text())
          .then(res => this.setState({ apiResponse: res }));
    }

    // componentWillMount() {
    //   this.callAPI();
    // }
    // state = {
    //   tracks: [],
    //   search: '',
    //   artist: null,
    //   searchType: 'album',
    // }

    getHashParams = () => {
      var hashParams = {};
      var e, r = /([^&;=]+)=?([^&;]*)/g,
          q = window.location.hash.substring(1);
      while ( e = r.exec(q)) {
         hashParams[e[1]] = decodeURIComponent(e[2]);
      }
      console.log(hashParams);
      return hashParams;
    }


    changeType = (e) => {
      this.setState({searchType:e.target.value})
    };


    handleClick = (event) => {
      event.preventDefault()
      this.callAPI()
      console.log(this.state.apiResponse);
      const baseUrl = 'https://api.spotify.com/v1/search?'
      let fetchUrl = baseUrl + "query=" + this.state.search + '&type=' + this.state.searchType
      // const albumUrl = 'https://api.spotify.com/v1/artists/';
      // const baseUrl = 'https://api.spotify.com/v1/albums/'
      // const fetchUrl = baseUrl + this.state.search + '/tracks'
      const auth_token = 'Bearer ' + this.state.apiResponse


      fetch( fetchUrl, {
        method: "GET",
        headers: {
          'Content-type': 'application/json',
          'Authorization': auth_token
        },
      })
      .then(resp => resp.json())
       .then(data => console.log(data))
      .then(items => this.setState({
        tracks: items
      }))

     }


  render() {
    return (
      <div className="App">
        <p className="App-intro">{this.state.apiResponse}</p>
        <form>
          <input onChange={event => {this.setState({search: event.target.value})}} value={this.state.search} type="text" placeholder="Search..." name="search"/>
          <button type="submit" onClick={this.handleClick} > <i className="fa fa-search"></i>Search</button>
          </form>
          <label>Choose a Genre:</label>

          <select defaultValue={this.state.searchType} onChange={this.changeType}>
            <option value="album">Album</option>
            <option value="artist">Artist</option>
            <option value="track">Track</option>
            <option value="album,artist,track">All</option>
          </select>

      </div>
    );
  }
}

export default App;
