import React, { Component } from 'react'
import Howl from "howler";

let sound;
class TrackCard extends Component{
    constructor(){
        super()
        this.state = {
           sort: []
        }
    }
    handleHover = () => {
      this.soundPlay()
    }

    render(){
        sound = this.props.data.preview_url;
        soundPlay = (src) => {
          const sound = new Howl({
            src,
            html5: true
          })
          sound.play()
        }
        return(
          <div className='track-card' style={{ backgroundImage: `url(${this.props.img.images[1].url})`}} onMouseEnter={this.handleHover} >
          </div>
        )
    }
}

export default TrackCard;
