import React, { Component } from 'react'
import {Howl} from "howler";

let gloplaying = false;
class TrackCard extends Component{
    constructor(){
        super()
        this.state = {
           sort: [],
           sound: null
        }
    }
    handleHover = () => {
      if (this.state.sound != null) {
          if (!this.state.sound.playing()) {
              this.state.sound.play();
              gloplaying = true;
          }
      }
    }
    handleHoverOut = () => {
      if (this.state.sound != null) {
          if (!this.state.sound.playing()) {}
          else {
            this.state.sound.stop();
            this.state.sound.unload();
            gloplaying = false;
          }
      }
    }
    idk = () => {
      console.log(gloplaying);
    }
    render(){
        let audioClip = [{sound: this.props.data.preview_url}];
        console.log(this.props.data.preview_url);

        this.state.sound = new Howl({
            src: [this.props.data.preview_url],
            html5: true,
            onplayerror: ()=> {this.state.sound.once('unlock', ()=> this.state.sound.play())}
          });

        return(
          <div className='track-card' onClick={this.idk}>
            <img src= {this.props.img.images[1].url}  onMouseEnter={this.handleHover} onMouseLeave={this.handleHoverOut} />
          </div>
        )
    }
}

export default TrackCard;
