import React, { Component } from 'react'


class TrackCard extends Component{
    constructor(){
        super()
        this.state = {
           sort: []
        }
    }

    handleHover = () => {
        let x = document.getElementById("myAudio")
        x.play();
    }

    // handlePause = () => {
    //     let x = document.getElementById("myAudio")
    //     x.pause()
    // }

    render(){
        console.log(this.props)
        return(
        <div>
          <audio id='myAudio' >
          <source src={this.props.data.preview_url} type="audio/mpeg" >
			       </source>
          </audio>
          <div className='track-card' style={{ backgroundImage: `url(${this.props.data.album.images[1].url})`}} onMouseEnter={this.handleHover} >
          </div>
        </div>
        )
    }
}

export default TrackCard;
