import React, { Component } from 'react'
import {Howl} from "howler";

let audioClip;
let sound;
class TrackCard extends Component{
    constructor(){
        super()
        this.state = {
           sort: []
        }
    }
    handleHover = () => {
      if (sound != null) {
          if (!sound.playing()) {
              sound.play();
          } else {
              sound.stop();
              sound.unload();
              sound.play();
          }
      }
    }
    handleHoverOut = () => {
    }
    soundPlay = (src) => {

      console.log(sound.playing());
      sound.play()


    }
    soundStop = (src) => {
      sound = new Howl({
        src,
        html5: true
      })
      sound.stop()
    }
    render(){
        audioClip = [{sound: this.props.data.preview_url}];
        sound = new Howl({
          src: audioClip["sound"],
          html5: true,
          onplayerror: ()=> {sound.once('unlock', ()=> sound.play())}
        });

        return(
          <div className='track-card' style={{ backgroundImage: `url(${this.props.img.images[1].url})`}} onMouseEnter={this.handleHover} onMouseLeave={this.handleHoverOut}>
          </div>
        )
    }
}

export default TrackCard;
