import React, { Component } from 'react'
import ReactHowler from 'react-howler';

let sound;
class TrackCard extends Component{
    constructor(){
        super()
        this.state = {
           sort: []
        }
    }
    handleHover = () => {
      sound.play()
    }
    handleHoverOut = () => {
      sound.stop()
    }
    render(){
        sound = <ReactHowler src= {this.props.data.preview_url} playing={false} />

        return(
          <div className='track-card' style={{ backgroundImage: `url(${this.props.img.images[1].url})`}} onMouseEnter={this.handleHover} onMouseLeave={this.handleHoverOut}>
          </div>
        )
    }
}

export default TrackCard;
