import React, { Component } from 'react'


class TrackCard extends Component{
    constructor(){
        super()
        this.state = {
           sort: []
        }
    }

    handleHover = () => {
        let x = document.getElementById("myAudio")
        x.play();
    }

    handlePause = () => {
        let x = document.getElementById("myAudio")
        x.pause()
    }

    render(){
         console.log(this.props)
        return(
        <div>
            <div className="track-card" >
                  <img onMouseOver={this.handleHover} onMouseLeave={this.handlePause} src={this.props.data.album.images[1].url}  alt='' />
            </div>
            <audio id='myAudio'>
                   {this.props.data.preview_url}
            </audio>
        </div>
        )
    }
}

export default TrackCard;
