import React, { Component } from 'react'
import {Howl} from "howler";


class TrackCard extends Component{
    constructor(){
        super()
        this.state = {
           sort: [],
           sound: null
        }
    }
    handleHover = () => {
      if (this.state.sound != null) {
          if (!this.state.sound.playing()) {
              this.state.sound.play();
          } else {
              this.state.sound.stop();
              this.state.sound.unload();
              this.state.sound.play();
          }
      }
    }
    handleHoverOut = () => {
      if (this.state.sound != null) {
          if (!this.state.sound.playing()) {
          } else {
              this.state.sound.stop();
              this.state.sound.unload();
          }
      }
    }

    render(){
        let audioClip = [{sound: this.props.data.preview_url}];
        console.log(this.props.data.preview_url);
        this.setState({
          sound : new Howl({
            src: [this.props.data.preview_url],
            html5: true,
            onplayerror: ()=> {this.state.sound.once('unlock', ()=> sound.play())}
          })
        });

        return(
          <div className='track-card' style={{ backgroundImage: `url(${this.props.img.images[1].url})`}} onMouseEnter={this.handleHover} onMouseLeave={this.handleHoverOut}>
          </div>
        )
    }
}

export default TrackCard;
